        private void SeedTour()
        {
            if (_context.Tours.Any())
                return;
            _context.Tours.AddRange(
                new Tour()
                {
                    TourName = "Hãy đi đến nước Anh",
                    HotelId = 1,
                    //RestaurantId = 1,
                    ShortDescription = "Tìm hiểu sâu về lịch sử và văn hóa của ba thành phố đầy cảm hứng và có tầm ảnh hưởng.",
                    Schedule = @"<p> <strong>Chuyến bay qua đêm</strong></p><p> 1 đêm</p><p> <strong>Ngày 1: Ngày du lịch</strong></p><p> Lên máy bay qua đêm chuyến bay tới Luân Đôn hôm nay.</p><p> <strong>London </strong>|<strong> 3 đêm</strong></p><p> <strong>Ngày 2: Đến Luân Đôn</strong> </p><p> Bữa ăn bao gồm: Bữa tối chào mừng</p><p> Chào mừng đến với nước Anh! Gặp gỡ những người bạn đồng hành của bạn tại bữa tối chào mừng tối nay.</p><p> <strong>Ngày 3: Chuyến tham quan tham quan Luân Đôn</strong></p><p> Bữa ăn bao gồm: Bữa sáng</p><p> Ăn uống chuyến tham quan có hướng dẫn đến các địa danh vương giả của thủ đô nước Anh với sự dẫn đường của một chuyên gia địa phương.</p><ul> <li>Đi qua Quảng trường Trafalgar tráng lệ và Nhà thờ St. Paul theo phong cách Baroque nằm trên đỉnh Đồi Ludgate </li> <li> Ngắm nhìn Tu viện Westminster, nơi tổ chức lễ đăng quang, đám cưới và tang lễ của hoàng gia </li> <li> Ngắm nhìn các biểu tượng như Big Ben, London Eye và Tháp London </li> <li> Dừng chân bên Cung điện Buckingham , nơi bạn có thể xem Nghi thức Đổi gác </li></ul><p> Tận hưởng buổi chiều tự do ở Luân Đôn hoặc thêm một chuyến du ngoạn.</p><p> <img src=""https://img2 .storyblok.com/1534x920/smart/f/53624/600x490/7dc9dfb8f9/windsor-castle-opwig.jpg""></p><p> Lâu đài Windsor</p><p> Từ 115 USD/người</p ><p><strong>Ngày 4: Ngày tự do ở Luân Đôn</strong></p><p> Bao gồm bữa ăn: Bữa sáng</p><p> Dành một ngày tự do ở Luân Đôn hoặc thêm các chuyến du ngoạn.</p> <p>  </p>",
                    TotalPrice = 3220000,
                    Description = "<h2> Tìm hiểu sâu về lịch sử và văn hóa của ba thành phố đầy cảm hứng và có ảnh hưởng.</h2><p> Có lẽ không thành phố nào có ảnh hưởng đến thế giới trong 2.000 năm qua hơn London, Paris và Rome. Trong chuyến tham quan có hướng dẫn này, các điểm tham quan mang tính biểu tượng như Big Ben, Tháp Eiffel và Đấu trường La Mã chỉ là khởi đầu của những gì bạn sẽ thấy. Tận hưởng tất cả khi bạn bắt đầu chuyến đi sâu rộng này qua Anh, Pháp và Ý.</p><p> Gói du lịch của bạn bao gồm</p><ul> <li> 3 đêm tại các khách sạn được lựa chọn cẩn thận </li> < li> 3 bữa sáng </li> <li> 1 bữa tối có bia hoặc rượu </li> <li> 1 chuyến tham quan </li><li>Chuyên gia Tour Director & hướng dẫn viên địa phương </li></ul><p > Các điểm nổi bật bao gồm</p><ul> <li> Quảng trường Trafalgar </li> <li> Cung điện Buckingham </li> <li> Đi tàu Eurostar </li> <li> Điểm dừng chân chụp ảnh Tháp Eiffel </li> < li> Place de la Concorde </li></ul><p>  </p>",
                    Status = "Hoạt động",
